import React, { FC, useState } from "react";
import Comment, { IComment } from "../../Comment/Comment";
import { useMutation, useQuery } from "@apollo/client";
import {
	LIKE_POST,
	ALL_POSTS,
	UNLIKE_POST,
	IS_LIKED_BY_USER,
	FIND_POST_BY_ID,
} from "../../../queries/post.query";

export interface IPost {
	id: string;
	user?: {
		firstName: string;
		lastName: string;
	};
	firstName: string;
	lastName: string;
	date: string;
	content: string;
	likes: number;
	comments: IComment[];
}

const Post = (props: IPost): JSX.Element => {
	// !TODO BUTTON SHOULD BE DISABLED WHILE LIKING OR UNLIKING
	//const [loading, setLoading] = useState<boolean>(false);

	const [like, { loading: likeLoading }] = useMutation(LIKE_POST, {
		refetchQueries: [
			{ query: FIND_POST_BY_ID, variables: { id: props.id } },
			{
				query: IS_LIKED_BY_USER,
				variables: { id: props.id, userId: localStorage.getItem("id") },
			},
		],
	});
	const [unLike, { loading: unlikeLoading }] = useMutation(UNLIKE_POST, {
		refetchQueries: [
			{ query: FIND_POST_BY_ID, variables: { id: props.id } },
			{
				query: IS_LIKED_BY_USER,
				variables: { id: props.id, userId: localStorage.getItem("id") },
			},
		],
	});

	const { data, loading } = useQuery(IS_LIKED_BY_USER, {
		variables: { id: props.id, userId: localStorage.getItem("id") },
		onError: (error) => {
			console.log("Error: ", error.message);
		},
	});

	const likeFunction = async () => {
		//	setLoading(true);
		data?.isLikedByUser
			? await unLike({
					variables: { id: props.id, userId: localStorage.getItem("id") },
			  })
			: await like({
					variables: { id: props.id, userId: localStorage.getItem("id") },
			  });

		//	setLoading(false);
	};

	const LikeUnLikeButton = (): JSX.Element => {
		const likeButton = (
			<button disabled={likeLoading} onClick={() => likeFunction()}>
				{" "}
				Like{" "}
			</button>
		);

		const unLikeButton = (
			<button disabled={unlikeLoading} onClick={() => likeFunction()}>
				{" "}
				Unlike{" "}
			</button>
		);
		return data?.isLikedByUser ? unLikeButton : likeButton;
	};

	return (
		<div>
			<div>
				<h4>
					{props.user?.firstName} {props.user?.lastName}{" "}
					<span>{props.date}</span>
				</h4>
			</div>
			<div>
				<p>{props.content}</p>
			</div>
			<div>{props.likes}</div> {LikeUnLikeButton()}
			<div>
				{props?.comments?.map((comment: IComment) => {
					<Comment
						firstName={comment.firstName}
						lastName={comment.lastName}
						content={comment.content}
						date={comment.date}
						likes={comment.likes}
						comments={comment.comments}
					/>;
				})}
			</div>
		</div>
	);
};

export default Post;
