import React from "react";
import { useQuery } from "@apollo/client";
import { v4 as uuid } from "uuid";
import Post, { IPost } from "./Post/Post";
import { ALL_POSTS } from "../../queries/post.query";
import { ALL_USERS } from "../../queries/user.query";
import { IUser } from "../Users/User/User";

const Posts = (): JSX.Element => {
	const result = useQuery(ALL_POSTS);
	if (result.loading) return <div>Loading...</div>;

	return (
		<div>
			<h3>Posts</h3>
			{result.data.allPosts.map((post: IPost) => (
				// <div key={uuid()}>
				// 	{post.content}

				// </div>
				<Post firstName={post.user.firstName} lastName={post.user.lastName} content={post.content} comments={post.comments} likes={post.likes}/>
			))}
		</div>
	);
};

export default Posts;
