					// 	date={post.date}
					// 	content={post.content}
					// 	likes={post.likes}
					// 	comments={post.comments}
					// />;
					<div key={uuid()}>
					{post.firstName}

				</div>
				})}
			</div>
		</div>
	);
};

export default Posts;
