import { gql } from "@apollo/client";

export const ALL_POSTS = gql`
	query allPosts {
		allPosts {
			user {
				firstName
				lastName
			}
			date
			content
			likes
		}
	}
`;

export const LIKE_POST = gql`
	mutation liek
`
