import React, { useState, useEffect } from "react";
import { useQuery, useMutation } from "@apollo/client";
import { SIGNIN } from "../../queries/user.query";

const SignIn = (): JSX.Element => {
	const [email, setEmail] = useState<string>("");
	const [password, setPassword] = useState<string>("");
	const [error, setError] = useState<boolean>(false);

	const [login, result] = useMutation(SIGN_IN, {
		onError: (error) => {
			setError(true);
			console.log(error.graphQLErrors[0].message);
		},
	});

	useEffect(() => {
		if (result.data) {
			const token = result.data.login.value;
			const firstName = result.data.login.firstName;
			const lastName = result.data.login.lastName;
			localStorage.setItem("token", token);
			localStorage.setItem("firstName", firstName);
			localStorage.setItem("lastName", lastName);
		}
	}, [result.data]);

	const handleForm = (event) => {
		// event.preventDefault();
		login({
			variables: { email, password },
		});
	};

	const handleSignOut = () => {
		localStorage.clear();
	};

	const SignInForm = (
		<div>
			<form onSubmit={handleForm}>
				<input
					type="text"
					name="email"
					value={email}
					onChange={({ target }) => setEmail(target.value)}
				/>
				<input
					type="password"
					name="password"
					value={password}
					onChange={({ target }) => setPassword(target.value)}
				/>
			</form>
			<button type='submit'>Submit</button>
		</div>
	);

	if (result.loading) return <div>Loading...</div>;
	const signOut = <button onClick={handleSignOut}>Sign Out</button>;

	return localStorage.getItem("token") ? signOut : SignInForm;
};

export default SignIn;
